	(2880, '2013-10-31 09:11:22', 158, 'SortRequest_private', 'filter domain sending an error back to domus.cobaltcascade.localhost domain for request:', '{"action":"RunRhythm","request_data":{"type":"stream","rhythm":{"domain":"cobaltcascade.localhost","username":"sky","name":"skys priority","version":"0/0/0","date_created":1371549635,"status":"public","description":"Order the results by the take value that user sky has given the main value.","js":"function () {\\r\\n    var sky_takes_fetched = false;\\r\\n    \\r\\n    var sky_takes_fetched_count = 0;\\r\\n    \\r\\n    var sky_takes = [];\\r\\n    \\r\\n    var stream_name;\\r\\n    \\r\\n    var stream_version;\\r\\n    \\r\\n    var stream_domain;\\r\\n    \\r\\n    var stream_username;    \\r\\n    \\r\\n    var offer;\\r\\n    \\r\\n    var onTakesFetched = function (block_numbers_length, data) {           \\r\\n        var data_length = data.takes.length;\\r\\n        for (var j = 0; j < data_length; j++) {                      \\r\\n            sky_takes[\'o_\' + data.takes[j].offer_id] = data.takes[j].value;\\r\\n        }\\r\\n        sky_takes_fetched_count++;\\r\\n        if (sky_takes_fetched_count === block_numbers_length) {\\r\\n            sky_takes_fetched = true;\\r\\n            calculateSort();\\r\\n        }\\r\\n    };\\r\\n    \\r\\n    var calculateSort = function () {  \\r\\n        var offer_id = offer.offer_id; \\r\\n        var sort = 0;\\r\\n        if (typeof sky_takes[\'o_\' + offer_id] !== \'undefined\') {\\r\\n            sort = sky_takes[\'o_\' + offer_id];\\r\\n        } else {\\r\\n            sort = 0;\\r\\n        }\\r\\n        BabblingBrook.Filter.processNextPost(sort);\\r\\n    };\\r\\n    \\r\\n    var onTakesFetchedError = function () {\\r\\n        console.error(\\"skys filter error. Fetching takes failed.\\");\\r\\n    };\\r\\n    var init = function () {\\r\\n        var stream_url = BabblingBrook.Filter.getStreamUrl();\\r\\n        stream_name = BabblingBrook.Library.extractName(stream_url);\\r\\n        stream_version = BabblingBrook.Library.extractVersion(stream_url);\\r\\n        stream_domain = BabblingBrook.Library.extractDomain(stream_url);\\r\\n        stream_username = BabblingBrook.Library.extractUsername(stream_url);         \\r\\n        BabblingBrook.Filter.processNextPost();\\r\\n    };\\r\\n    \\r\\n    var main = function (current_offer) {\\r\\n        offer = current_offer;\\r\\n        if (sky_takes_fetched === false) {            \\r\\n            var block_numbers_length = BabblingBrook.Filter.getBlockNumbers().length;          \\r\\n            for (var i = 0; i < block_numbers_length; i++) {      \\r\\n                var block_number = BabblingBrook.Filter.getBlockNumbers()[i];\\r\\n                var url = stream_domain + \'/\' + stream_username + \'/stream/\' + \\r\\n                        stream_name + \'/\' + stream_version + \'/getusertakes?username=sky&block_number=\' + \\r\\n                        block_number + \'&type=stream\';\\r\\n\\t\\t\\t\\tBabblingBrook.Filter.getMiscData(url, onTakesFetched.bind(null, block_numbers_length));           \\t\\r\\n            }\\r\\n        } else {\\r\\n            calculateSort();\\r\\n        }\\r\\n    };\\r\\n    \\r\\n    return {\\r\\n        init : init,\\r\\n        main : main\\r\\n    };\\r\\n}"},"stream":{"url":"cobaltcascade.localhost/sky/stream/news/0/0/0/json","offers_from_timestamp":null,"offers_to_timestamp":null},"user":{"username":"sky","domain":"cobaltcascade.localhost"},"client_domain":"cobaltcascade.localhost"},"error_code":"SortRequest_private","error_data":{"action":"GetOffers","request_data":{"sort_qty":1000,"offers_from_timestamp":null,"offers_to_timestamp":null,"with_content":false},"error_code":"GetOffers_private","error_data":"filter_getprivateoffers","request_guid":"75FC66B4-54EC-412D-BBD4-23B87603BD54"},"request_guid":"E69A86CF-CADD-47EB-84AB-0360C3E46C11"}'),
