	(10011, '2013-06-21 14:58:05', 10011, 'Displays the most popular posts from the last hour. Weighting more recent posts with a higher score.', '\nfunction(){var block_takes_fetched=[];var calculate_ready=false;var takes={};var stream_name;var stream_version;var stream_domain;var stream_username;var now;var an_hour_ago;var offer;var calculateSort=function(){var value=0;if(offer.timestamp<an_hour_ago||offer.timestamp>now){value=-10000;}else{for(var domain in takes[offer.offer_id]){for(var username in takes[offer.offer_id][domain]){value+=parseInt(takes[offer.offer_id][domain][username].value);}}}\nBabblingBrook.Filter.processNextPost(value);};var onTakesFetchedError=function(){console.error("error fetching takes for popular in hour sort");};var onTakesFetched=function(block_number,block_takes){jQuery.extend(takes,block_takes);block_takes_fetched.push(block_number);if(block_takes_fetched.length===BabblingBrook.Filter.getBlockNumbers().length){calculate_ready=true;calculateSort();}};var init=function(){var stream_url=BabblingBrook.Filter.getStreamUrl();stream_name=BabblingBrook.Library.extractName(stream_url);stream_version=BabblingBrook.Library.extractVersion(stream_url);stream_domain=BabblingBrook.Library.extractDomain(stream_url);stream_username=BabblingBrook.Library.extractUsername(stream_url);now=Math.round(new Date().getTime()/1000);an_hour_ago=now-(60*60);BabblingBrook.Filter.processNextPost();};var main=function(current_offer){offer=current_offer;if(calculate_ready===false){var block_numbers_length=BabblingBrook.Filter.getBlockNumbers().length;for(var i=0;i<block_numbers_length;i++){var block_number=BabblingBrook.Filter.getBlockNumbers()[i];var url=stream_domain+\'/\'+stream_username+\'/stream/\'+\nstream_name+\'/\'+stream_version+\'/getstreamtakes?username=sky&block_number=\'+block_number;BabblingBrook.Filter.getMiscData(url,onTakesFetched.bind(null,block_number));}}else{calculateSort();}};return{init:init,main:main};}', '/**\r\n * Fetches all the takes for all