INSERT INTO `user_config_default` (`user_config_default_id`, `display_order`, `code`, `name`, `description`, `type_id`, `default_value`, `extra_data`) VALUES
	(1, 1, 'stream_rhythm_suggestion_url', 'A rhythm that suggests new streams that you may be interested in', 'This rhythm is used by both the suggestions box and on the edit streams page to display new streams that you may be interested in subscribing to.', 153, 'cobaltcascade.localhost/sky/rhythm/basic+stream+suggestion/0/0/0/json', '{"rhythm_cat" : "stream suggestion"}'),
	(2, 2, 'stream_filter_rhythm_suggestion_url', 'A rhythm that suggests new filters for streams you are subscribed to', 'This rhythm is used by both the suggestions box and on the edit streams page to display new sort filters for streams that you may be interested in subscribing to.', 153, 'cobaltcascade.localhost/sky/rhythm/basic+filter+suggestion/0/0/0/json', '{"rhythm_cat" : "stream filter suggestion"}'),
	(5, 100, 'stream_expiry', 'How many seconds to wait before cached stream data is considered out of date.', 'Stream data is cached locally to speed up the process of displaying posts to you when you view a stream. After the number of seconds set here, the stream and post data will be refetched.', 155, '600', NULL),
	(6, 101, 'iframe_timeout', 'How many milliseconds to wait before the Babbling Brook frames have to have loaded and registered themselves.', 'When data is fetched from remote domains, this sets how long to wait before timing out and displaying an error.', 155, '10000', NULL),
	(7, 102, 'filter_timeout', 'How many milliseconds to wait for filter rhythms to report thair results.', 'When data is rhythms are started to filter rhythm results, this sets how long they have to perform before timing out and displaying an error.', 155, '60000', NULL),
	(8, 103, 'settimeout_timeout', 'How many milliseconds to wait before a javascript wait function times out.', 'There are many instances where the code has to pause and wait for data to be fetched. This is the default amount of time to wait before timing out.', 155, '10000', NULL),
	(9, 104, 'action_timeout', 'How long to wait for javascript actions to timeout.', 'The amount of time to wait when requesting data from remote domains before timing out.', 155, '60000', NULL),
	(10, 5, 'user_stream_rhythm_suggestion_url', 'A rhythm that suggests new streams for rating users', 'This rhythm is used by both the suggestions box and on the profile page to display new streams for rating users that you may be interested in subscribing to.', 153, 'cobaltcascade.localhost/sky/rhythm/basic+user+stream+suggestion/0/0/0/json', '{"rhythm_cat" : "user stream suggestion"}'),
	(11, 6, 'stream_ring_rhythm_suggestion_url', 'A rhythm that suggests rings that can be used to moderate streams', 'This rhythm is used by both the suggestions box and on the edit streams page to display new rings for moderating streams that you may be interested in subscribing to.', 153, 'cobaltcascade.localhost/sky/rhythm/basic+moderation+ring+suggestion/0/0/0/json', '{"rhythm_cat" : "stream ring suggestion"}'),
	(12, 8, 'user_rhythm_suggestion_url', 'A rhythm that suggests users you might be interested in', 'This rhythm is used by both the suggestions box and the profile page to display new users that you may be interested in.', 153, 'cobaltcascade.localhost/sky/rhythm/basic+user+suggestion/0/0/0/json', '{"rhythm_cat" : "user suggestion"}'),
	(13, 9, 'meta_rhythm_suggestion_url', 'A rhythm that suggests new suggestion rhythms that you might be interested in', 'This rhythm is used the suggestions box to display new suggestion rhythms that you may be interested in.', 153, '@todo', '{"rhythm_cat" : "meta suggestion"}'),
	(14, 10, 'kindred_rhythm_suggestion_url', 'A rhythm that suggests new kindred rhythms that you might be interested in', 'This rhythm is used the suggestions box to display kindred rhythms that you may be interested in.', 153, '@todo', '{"rhythm_cat" : "kindred suggestion"}'),
	(15, 105, 'ajax_timeout', 'How long to wait before an ajax request times out.', 'How long to wait for the server to respond when an ajax request is made.', 155, '30000', NULL),
	(16, 106, 'snippets_to_store', 'How many snippets should be saved in local storage.', 'Snippets are stored in local storage so that they can be displayed quickly when the website is first loaded. This setting decides how many to save. A larger number means that user has more content to read whilst waiting for fresh data.', 155, '30', NULL),
	(17, 107, 'message_box_lines', 'How many lines should the message box show when minimized.', 'The message box can take up more or less space. The lowest possible setting is 2.', 155, '2', NULL),
