INSERT INTO `rhythm_extra` (`rhythm_extra_id`, `date_created`, `rhythm_id`, `description`, `mini`, `full`, `status_id`, `version_id`, `rhythm_cat_id`, `meta_post_id`) VALUES
	(10000, '2011-11-08 09:10:40', 10000, 'Sort items by date, with the oldest first.', '\nfunction(){var main=function(post){var sort_value=10000000000-post.timestamp;BabblingBrook.Filter.processNextPost(sort_value);};return{main:main};}', 'function () {\r\n	var main = function (post) {\r\n      var sort_value = 10000000000 - post.timestamp;\r\n      BabblingBrook.Filter.processNextPost(sort_value);\r\n	};\r\n  \r\n	return {\r\n      main : main\r\n	};\r\n}', 2, 10008, 1, 10006),
	(10002, '2013-06-14 09:20:07', 10002, 'Sort items by date, with the newest first.', '\nfunction(){var main=function(post){var sort_value=post.timestamp;BabblingBrook.Filter.processNextPost(sort_value);};return{main:main};}', 'function () {\r\n	var main = function (post) {\r\n      var sort_value = post.timestamp;\r\n      BabblingBrook.Filter.processNextPost(sort_value);\r\n	};\r\n  \r\n	return {\r\n      main : main\r\n	};\r\n}', 2, 10014, 1, 10013),
	(10008, '2013-06-18 10:00:35', 10008, 'Order the results by the take value that user sky has given the main value.', '/**\n * Fetches all the takes for all the blocks that posts have been fetched from.\n * It then sums the takes for each post in the last day and discards others.\n * \n */\nfunction () {\n  \n    var priority_user_domain = \'cobaltcascade.localhost\';\n    \n    var priority_user_username = \'sky\';\n  \n    var calculate_ready = false;\n  \n    var streams_with_takes = {};\n  \n    var now;\n  \n    var an_hour_ago;\n    \n    var post;\n        \n    var calculateSort = function () {\n        var value = 0;         \n        var post_stream_version = BabblingBrook.Library.makeVersionObject(post.stream_version);\n        var does_take_exist = BabblingBrook.Library.doesNestedObjectExist(streams_with_takes, [\n            post.stream_domain,\n            post.stream_username,\n            post.stream_name,\n            post_stream_version.major,\n            post_stream_version.minor,\n            post_stream_version.patch,\n            post.post_id\n        ]);        \n        if (does_take_exist === true) {     \n            var take_stream = streams_with_takes[post.stream_domain][post.stream_username][post.stream_name];\n            var take_version = take_stream[post_stream_version.major][post_stream_version.minor][post_stream_version.patch];\n            var take = take_version[post.post_id];        \n            value += parseInt(take.value);\n        }\n        BabblingBrook.Filter.processNextPost(value);\n    };\n    \n    var onTakesFetchedError = function () {\n        console.error("error fetching takes for popular in hour sort");\n    };\n    \n    var onTakesFetched = function (streams) {\n        streams_with_takes = streams;  \n        calculate_ready = true;\n        calculateSort();\n    };\n  \n    var init = function () {              \n        now = Math.round(new Date().getTime() / 1000);\n        an_hour_ago = now - (60 * 60);\